import           Data.Aeson                   (decode)
import           Data.ByteString.Lazy.Char8   (ByteString)
import qualified Data.ByteString.Lazy.Char8   as BSL
import           Network.HTTP.Client.Internal (CookieJar (..), Response (..),
                                               ResponseClose (..))
import           Network.HTTP.Simple
import           Network.HTTP.Types.Status
import           Network.HTTP.Types.Version

import           Twitter
import           Twitter.API

data RawTweet = RawTweet
  { tID   :: Integer
  , tJSON :: ByteString
  }

type MockBackend = [RawTweet]

fullResponseBody :: MockBackend -> ByteString
fullResponseBody tweets = "{\"statuses\":[" <> BSL.intercalate "," (map tJSON tweets) <> "]}"

fullResponse :: MockBackend -> Response BSL.ByteString
fullResponse tweets = Response
  { responseStatus = status200
  , responseVersion = http11
  , responseHeaders = []
  , responseBody = fullResponseBody tweets
  , responseCookieJar = CJ []
  , responseClose' = ResponseClose $ return ()
  }

instance APIBackend MockBackend where
  getAPIAnswer tweets req = do
    let params = fromJust $ fromQueryParams $ getRequestQueryString req
        count = fromMaybe 30 $ _count params
        maxId = fromMaybe 999999999999999999 $ _maxId params
        retList = take count $ dropWhile (\t -> tID t > maxId) tweets
    return $ fullResponse retList

mockApp :: TwitterApp Authorized
mockApp = TwitterApp "" "" (Credential "") testServer

testServer :: MockBackend
testServer = zipWith (flip RawTweet) rawTweets $ map (_id . fromJust . decode) rawTweets

